                Console.WriteLine("                                                                         You continue to walk forwards until you can no longer hear anything...");
                Task.Delay(EerieWaitTime).Wait();
                Console.Clear();

                Console.WriteLine("                                                                                                        . . .");
                Task.Delay(LongWaitTime).Wait();
                Console.Clear();
                Console.WriteLine("                                                                                                ...or feel anything.");
